    public void centerWindow(JInternalFrame frame){
        panelPrincipal.add(frame);
        Dimension dm = panelPrincipal.getSize();
        Dimension dmv = frame.getSize();
        frame.setLocation((dm.width - dmv.height )/2, (dm.height - dmv.width)/2);
        frame.show();
    }

